mapper.select:
select a.Website_ID,a.WebsiteName,a.Website from m_websiteinfo a where a.Website_ID not in (select b.Website_ID from m_classificationinfosource b where b.Classification_ID=#{Classification_ID} )
and a.WebsiteName like CONCAT('%',#{WebsiteName},'%')
and CollectionField_ID=#{CollectionField_ID} and ValidFlag='1' order by a.WebsiteName asc limit #{l_pre},#{rowSize}

mapper.delete:
delete from m_classificationinfosource where Classification_ID=#{p1} 
  and Website_ID in
  #{item}
