mapper.delete:
delete from m_mycollection

	User_ID = #{userid}

		and CollectionType_ID = #{collectiontypeid}


		and Article_ID in (${articleId})

mapper.update:
update m_mycollection

	CollectionType_ID = #{updateTypeId}

where User_ID = #{userid}
and Article_ID in
(${articleId})

mapper.select:
SELECT a.Article_ID articleId,a.CollectionType_ID collectiontypeId,a.DisplayOrder displayorder,a.CreateDateTime createdatetime,a.CreateUser createuser, a.UpdateDateTime updatedatetime,a.UpdateUser updateuser,a.User_ID userId,b.ArticleTitle articleTitle,b.Releasetime releasetime, (select GROUP_CONCAT(CollectionTypeName ORDER BY CollectionStratum asc SEPARATOR '>') FROM m_collectiontype WHERE LOCATE(CollectionType_ID, (select DISTINCT getParentList_collectiontype(c.CollectionType_ID) from m_collectiontype))>0 ) allparentname FROM (m_mycollection a INNER JOIN a_articlebasicinfo b ON a.Article_ID=b.Article_ID) INNER JOIN m_collectiontype c ON a.CollectionType_ID = c.CollectionType_ID
and a.User_ID=#{userId}
and a.CollectionType_ID = #{collectiontypeId}
and b.ArticleTitle like concat("%",#{articleTitle},"%") or b.ArticleAbstract like concat("%",#{articleTitle},"%") or b.ArticleText like concat("%",#{articleTitle},"%")
ORDER BY c.CollectionStratum,c.CollectionPath,a.DisplayOrder ASC limit #{l_pre},#{rowSize}

SELECT count(1) FROM m_mycollection a INNER JOIN a_articlebasicinfo b ON a.Article_ID=b.Article_ID
and a.User_ID=#{userId}
and a.CollectionType_ID = #{collectiontypeId}
and b.ArticleTitle like concat("%",#{articleTitle},"%") or b.ArticleAbstract like concat("%",#{articleTitle},"%") or b.ArticleText like concat("%",#{articleTitle},"%")

