mapper.select:

  select count(1) from a_articlebasicinfo where 
     Releasetime>=#{montime_start} and Releasetime <= #{montime_end} 

         and locate(#{Classification_ID},ClassificationPath)>0



             and (ArticleText like CONCAT('%',#{search_key},'%') or ArticleTitle like CONCAT('%',#{search_key},'%') or ArticleAbstract like CONCAT('%',#{search_key},'%'))


             and ArticleTitle like CONCAT('%',#{search_key},'%')


             and ArticleAbstract like CONCAT('%',#{search_key},'%')


             and ArticleText like CONCAT('%',#{search_key},'%')


and Article_ID not in (select Article_ID from A_UserDeleteArticleInfo where User_ID=#{userid})

     and EmotionDivision=#{emoana}


      and SourceDivision=#{infsour}

select count(1) From a_articlebasicinfo Where Article_ID In (Select Max(dd.Article_ID) From (
select Article_ID,SimilarArticlesNo from a_articlebasicinfo where 1=1 and Releasetime>=#{montime_start} and Releasetime <= #{montime_end}
and locate(#{Classification_ID},ClassificationPath)>0
and (ArticleText like CONCAT('%',#{search_key},'%') or ArticleTitle like CONCAT('%',#{search_key},'%') or ArticleAbstract like CONCAT('%',#{search_key},'%'))
and ArticleTitle like CONCAT('%',#{search_key},'%')
and ArticleAbstract like CONCAT('%',#{search_key},'%')
and ArticleText like CONCAT('%',#{search_key},'%')
and Article_ID not in (select Article_ID from A_UserDeleteArticleInfo where User_ID=#{userid})
and EmotionDivision=#{emoana}
and SourceDivision=#{infsour}
) dd Group By dd.SimilarArticlesNo)

SELECT c.*, (select m.WebsiteName from m_websiteinfo m where m.Website_ID = c.Website_ID) WebsiteName, ( select count(1) from a_articlebasicinfo bb where c.SimilarArticlesNo=bb.SimilarArticlesNo and c.Article_ID!=bb.Article_ID and not exists (select 1 from A_UserDeleteArticleInfo b where b.Article_ID=bb.Article_ID and b.User_ID=#{userid}) ) sim_cnt, (select count(1) from m_mycollection cc where cc.Article_ID=c.Article_ID ) collcnt, IF(c.Article_ID in (SELECT k.Article_ID FROM a_userearlywarningarticleinfo as k where k.User_ID=#{userid}),"yes","no") isearlywarning FROM a_articlebasicinfo c WHERE c.Article_ID IN ( Select Max(dd.Article_ID) From
(select Article_ID,SimilarArticlesNo from a_articlebasicinfo where 1=1 and Releasetime>=#{montime_start} and Releasetime <= #{montime_end}
and locate(#{Classification_ID},ClassificationPath)>0
and (ArticleText like CONCAT('%',#{search_key},'%') or ArticleTitle like CONCAT('%',#{search_key},'%') or ArticleAbstract like CONCAT('%',#{search_key},'%'))
and ArticleTitle like CONCAT('%',#{search_key},'%')
and ArticleAbstract like CONCAT('%',#{search_key},'%')
and ArticleText like CONCAT('%',#{search_key},'%')
and Article_ID not in (select Article_ID from A_UserDeleteArticleInfo where User_ID=#{userid})
and EmotionDivision=#{emoana}
and SourceDivision=#{infsour}
) dd Group By dd.SimilarArticlesNo )
order by Releasetime desc
order by Releasetime asc
limit #{l_pre},#{rowSize}

select a.*, (select m.WebsiteName from m_websiteinfo m where m.Website_ID = a.Website_ID) WebsiteName, ( select count(1) from a_articlebasicinfo b where a.SimilarArticlesNo=b.SimilarArticlesNo and a.Article_ID!=b.Article_ID and not exists (select 1 from A_UserDeleteArticleInfo where Article_ID=b.Article_ID and User_ID=#{userid}) ) sim_cnt, (select count(1) from m_mycollection c where c.Article_ID=a.Article_ID ) collcnt, IF(a.Article_ID in (SELECT k.Article_ID FROM a_userearlywarningarticleinfo as k where k.User_ID=#{userid}),"yes","no") isearlywarning from a_articlebasicinfo a where Releasetime>=#{montime_start} and Releasetime <= #{montime_end}
and locate(#{Classification_ID},ClassificationPath)>0
and (ArticleText like CONCAT('%',#{search_key},'%') or ArticleTitle like CONCAT('%',#{search_key},'%') or ArticleAbstract like CONCAT('%',#{search_key},'%'))
and ArticleTitle like CONCAT('%',#{search_key},'%')
and ArticleAbstract like CONCAT('%',#{search_key},'%')
and ArticleText like CONCAT('%',#{search_key},'%')
and Article_ID not in (select Article_ID from A_UserDeleteArticleInfo where User_ID=#{userid})
and EmotionDivision=#{emoana}
and SourceDivision=#{infsour}
order by Releasetime desc
order by Releasetime asc
limit #{l_pre},#{rowSize}

select count(1) from a_articlebasicinfo where 
  Releasetime>#{releasetime} 

    and locate(#{Classification_ID},ClassificationPath)>0


         select a.Article_ID,a.ArticleTitle,a.ArticleURL from a_articlebasicinfo a where a.SimilarArticlesNo=
			(select b.SimilarArticlesNo from a_articlebasicinfo b where b.Article_ID=#{Article_ID}) and a.Article_ID!=#{Article_ID}
			and not exists (select 1 from A_UserDeleteArticleInfo b where b.Article_ID=a.Article_ID and b.User_ID=#{userid})
	      order by Releasetime desc 
          limit 0,#{rowSize}
